import React, { Component } from "react";

import "../../styles/SessionController.css";

import PlayButton from "../../resources/images/PlayButton.png";
import PauseButton from "../../resources/images/PauseButton.png";

class SessionController extends Component {
  state = {
    isPlaying: false,
  };

  playButtonPress = () => {
    if (this.state.isPlaying) {
      this.setState({ isPlaying: false });
      console.log(this.state.isPlaying);
    } else {
      this.setState({ isPlaying: true });
      console.log(this.state.isPlaying);
    }
  };

  render() {
    return (
      <div class="controllerDiv">
        <div class="controllerButtons">
          <button class="restartButton"></button>
          <button
            class="playButton"
            background={this.state.isPlaying ? PauseButton : PlayButton}
            onClick="playButtonPress()"></button>
          <button class="skipButton"></button>
        </div>
      </div>
    );
  }
}

export default SessionController;
