import React, { Component } from "react";

import "../../styles/SessionController.css";

import {
  play,
  pause,
  skipSongSpotify,
  queueSpotifySong,
  playURI,
} from "../../utils/spotifyInterface";

import { popQueue } from "../../utils/queueInterface";

class SessionController extends Component {
  state = {
    isPlaying: global.isContentPlaying,
    currentTrack: {
      songTitle: "Ghost Voices (Hex Cougar Flip)",
      artist: "Hex Cougar",
      albumImgURL: require("../../resources/images/GhostVoicesHex.png"),
    },
  };

  playButtonPress = () => {
    if (global.isContentPlaying) {
      global.isContentPlaying = false;
      this.setState({ isPlaying: false });
      pause(global.spotifyAccessToken);
    } else {
      global.isContentPlaying = true;
      this.setState({ isPlaying: true });
      play(global.spotifyAccessToken);
    }
  };

  skipPressed = () => {
    if (global.sessionQueue.length === 0) {
      console.log("queue empty");
      return;
    }

    // Pop Queue
    var upNext = popQueue();

    // Read next song and determine platform
    if (upNext[0] == "Spotify") {
      var songURI = upNext[1]["uri"];

      playURI(songURI, global.spotifyAccessToken);

      global.isContentPlaying = true;

      // Set song as currently playing
      global.currentlyPlaying = upNext;

      this.setState({});
    }
  };

  // Spotify Web Api funcs this.state.currentTrack.albumImgURL

  render() {
    return (
      <div className="controllerDiv">
        <img
          className="currentTrackPic"
          src={global.currentlyPlaying[1].album.images[0].url}
          alt=""></img>
        <div className="controllerButtons">
          <button className="restartButton"></button>
          <button
            className="playButton"
            style={{
              background: global.isContentPlaying
                ? "url(" +
                  require("../../resources/images/PauseButton.png") +
                  ")"
                : "url(" +
                  require("../../resources/images/PlayButton.png") +
                  ")",
              left: this.state.isPlaying ? "-5px" : "",
            }}
            onClick={this.playButtonPress}></button>
          <button className="skipButton" onClick={this.skipPressed}></button>
        </div>
        <div className="songText">
          <p id="songTitle">{this.state.currentTrack.songTitle}</p>
          <p id="songArtist">{this.state.currentTrack.artist}</p>
        </div>
      </div>
    );
  }
}

export default SessionController;
