import React, { Component } from "react";

import "../../styles/SearchWeb.css";

import SearchResults from "./searchResults";

import SearchIcon from "../../resources/images/SearchIcon.png";

import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";

class SearchWeb extends Component {
  state = {};

  spotifySearchTrack() {
    var query = document.getElementById("searchQuery").value;
    spotifyWebApi.searchTracks(query).then(
      (response) => {
        console.log(response);
      },
      (reason) => {
        this.createNotification("error");
        console.error(reason); // Error!
      }
    );
  }

  createNotification = (type) => {
    return () => {
      switch (type) {
        case "info":
          NotificationManager.info("Info message");
          break;
        case "success":
          NotificationManager.success("Success message", "Title here");
          break;
        case "warning":
          NotificationManager.warning(
            "Warning message",
            "Close after 3000ms",
            3000
          );
          break;
        case "error":
          NotificationManager.error(
            "Error: Make sure you are logged into your Spotify",
            "Click me!",
            5000,
            () => {
              alert("callback");
            }
          );
          break;
      }
    };
  };

  render() {
    return (
      <div className="searchFlex">
        <div className="searchMiddle">
          <div className="searchQueryDiv">
            <img className="searchIcon" src={SearchIcon} alt=""></img>
            <div className="searchInputDiv">
              <input
                type="text"
                id="searchQuery"
                placeholder="Search a song or artist.."></input>
            </div>
            <div className="searchButtonDiv">
              <button
                id="spotifySearchBtn"
                onClick={this.spotifySearchTrack}></button>
            </div>
          </div>
          <SearchResults></SearchResults>
        </div>
        <div className="searchRight"></div>
      </div>
    );
  }
}

export default SearchWeb;
