import React, { Component } from "react";

import "../../styles/LibraryWeb.css";
import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
import "react-notifications/lib/notifications.css";

import LibraryResults from "./libraryResults";

import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

class LibraryWeb extends Component {
  state = {};

  createNotification = (type) => {
    console.log("GOT TO CREATE NOTIFICATION");
    switch (type) {
      case "info":
        NotificationManager.info("Info message");
        break;
      case "success":
        NotificationManager.success("Success message", "Title here");
        break;
      case "warning":
        NotificationManager.warning(
          "Warning message",
          "Close after 3000ms",
          3000
        );
        break;
      case "error":
        NotificationManager.error(
          "Make sure you are logged into your Spotify",
          "Error!",
          5000,
          () => {
            //alert("callback");
          }
        );
        break;
    }
    return () => {};
  };

  getSpotifyLibrary = () => {
    var query = document.getElementById("searchQuery").value;
    spotifyWebApi.searchTracks(query).then(
      (response) => {
        console.log(response);
        this.setState({
          trackSearchResults: response.tracks.items,
          spotifySearch: true,
        });
      },
      (reason) => {
        console.log("GOT TO ERROR");
        this.createNotification("error");
        console.error(reason); // Error!
      }
    );
  };

  render() {
    return (
      <div className="libraryFlex">
        <div className="libraryMiddle">
          <div className="libraryQueryDiv">
            <div className="libraryTabDiv">
              <button class="tablinks" id="defaultOpen">
                Favorites
              </button>
              <button class="tablinks">Playlists</button>
            </div>
            <div className="libraryButtonDiv">
              <button
                id="spotifyLibraryBtn"
                onClick={this.getSpotifyLibrary}></button>
            </div>
            <p id="libraryDivHeader">
              {this.state.spotifySearch
                ? "Spotify"
                : "Click desired platform to see library.."}
            </p>
          </div>
          <LibraryResults
            libraryPullResults={this.state.libraryPullResults}></LibraryResults>
          <NotificationContainer />
        </div>
        <div className="libraryRight"></div>
      </div>
    );
  }
}

export default LibraryWeb;
