import React, { Component } from "react";

import "../../styles/SessionWeb.css";

import SessionController from "./sessionController";
import SessionQueue from "./sessionQueue";
import SessionRight from "./sessionRight";

class SessionWeb extends Component {
  state = {
    songOptionsSelected: false,
    selectedContentInfo: "",
    selectedPlatform: "Spotify",
  };

  updateSessionState = () => {
    this.setState({});
  };

  showContentOptions = (songInfo) => {
    console.log(songInfo);

    this.setState({
      songOptionsSelected: true,
      selectedContentInfo: songInfo,
      selectedPlatform: songInfo[0]
    });
  }

  render() {
    return (
      <React.Suspense fallback={<div>Loading...</div>}>
        <div className="sessionFlex">
          <div className="sessionMiddle">
            <SessionController></SessionController>
            <SessionQueue showContentOptions={this.showContentOptions}></SessionQueue>
          </div>
          <SessionRight songOptionsSelected={this.songOptionsSelected} selectedPlatform={this.selectedPlatform} selectedContentInfo={this.selectedContentInfo}></SessionRight>

        </div>
      </React.Suspense>
    );
  }
}

export default SessionWeb;
