import React, { Component } from "react";

import "../../styles/PlaylistItem.css";

import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
import "react-notifications/lib/notifications.css";

import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

class PlaylistItem extends Component {
  state = {
    playlistTracks: {},
  };

  createNotification = (type) => {
    console.log("GOT TO CREATE NOTIFICATION");
    switch (type) {
      case "info":
        NotificationManager.info("Info message");
        break;
      case "success":
        NotificationManager.success("Success message", "Title here");
        break;
      case "warning":
        NotificationManager.warning(
          "Warning message",
          "Close after 3000ms",
          3000
        );
        break;
      case "error":
        NotificationManager.error(
          "Make sure you are logged into your Spotify",
          "Error!",
          5000,
          () => {
            //alert("callback");
          }
        );
        break;
    }
    return () => {};
  };

  getPlaylistTracks = (playlistID) => {
    spotifyWebApi.getPlaylistTracks(playlistID).then(
      (response) => {
        this.setState({
          playlistTracks: response.items,
        });
      },
      (reason) => {
        console.log("GOT TO ERROR");
        this.createNotification("error");
        console.error(reason); // Error!
      }
    );
  };

  render() {
    var { playlistInfo } = this.props;

    const playlistImgPath = playlistInfo.images[0].url;

    playlistID = playlistInfo.id;

    getPlaylistTracks(playlistID);

    return (
      <div className="playlistItemDiv">
        <img className="playlistImg" src={playlistImgPath} alt=""></img>
        <div className="playlistInfoDiv">
          <p className="playlistTitle">{playlistInfo.name}</p>
          <p className="numTracks">{playlistInfo.tracks.total}</p>
        </div>
        <div className="playlistOptionsDiv">
          <button className="playlistOptions"></button>
        </div>
        <NotificationContainer />
      </div>
    );
  }
}

export default PlaylistItem;
