import React, { Component } from "react";

import "../../styles/AccountsWeb.css";

import "../../global.js";

import spotifyLogo from "../../resources/images/Spotify.png";

import $ from "jquery";

import Spotify from "spotify-web-api-js";
const spotifyWebApi = new Spotify();

class AccountsWeb extends Component {
  constructor() {
    super();
    const params = this.getHashParams();

    this.state = {
      loggedIn: params.access_token ? true : false,
      accessToken: params.access_token,
    };
    if (this.state.access_token) {
      spotifyWebApi.setAccessToken(params.access_token);
      this.getUserData(params.access_token).then((response) => {
        this.setState({
          userImage: response.images[0].url,
        });
      });
      // this.setState({
      //   userImage: userData.images[0].url,
      // });
    }
  }

  getHashParams() {
    var hashParams = {};
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }

    if (this.state.loggedIn) {
      this.setState({
        loggedIn: true,
        accessToken: this.state.access_token,
      });
    }

    return hashParams;
  }

  // getUserData() {
  //   spotifyWebApi.getUser(this.state.accessToken).then((response) => {
  //     console.log(response);
  //     this.setState({
  //       userImage: response.images[0].url,
  //     });
  //   });
  // }

  getUserData(accessToken) {
    return $.ajax({
      url: "https://api.spotify.com/v1/me",
      headers: {
        Authorization: "Bearer " + accessToken,
      },
    });
  }

  render() {
    return (
      <div className="accountsDiv">
        <div className="spotifyBox">
          <p id="spotifyHeader">Spotify</p>
          <img id="spotifyLogo" src={spotifyLogo} alt=""></img>
          {this.state.loggedIn == false ? (
            <a href="http://localhost:8888">
              <button id="loginButton">Login to your Spotify</button>
            </a>
          ) : (
            <div id="loggedInDiv">
              <p id="loggedIn">You are logged in with Spotify.</p>
              <img
                className="userImage"
                src={this.state.userImage}
                alt=""></img>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default AccountsWeb;
