import React, { Component } from "react";

import "../../styles/queueItem.css";

import spotifyIcon from "../resources/images/Spotify.png";
//"../../resources/images/GhostVoices.png"
class QueueItem extends Component {
  state = {};

  formatArtistString = (artistObj) => {
    var artistString = "";
    var numArtists = 0;

    for (var key in Object.keys(artistObj)) {
      var artist = artistObj[key];

      if (numArtists > 0) {
        artistString = artistString + ", " + artist.name;
      } else {
        artistString = artist.name;
      }
      numArtists += 1;
    }

    return artistString;
  };

  render() {
    const { songInfo } = this.props;
    const platform = songInfo[0];
    const trackInfo = songInfo[1];
    const albumImgPath = trackInfo.album.images[0].url;
    return (
      <div className="queueItemDiv">
        <img className="albumImg" src={albumImgPath} alt=""></img>
        <div className="songInfoDiv">
          <div className="titleAndIcon">
            <p className="songTitle">{trackInfo.name}</p>
            <img src={spotifyIcon} className="spotifyIcon"></img>
          </div>
          <p className="songArtist">
            {this.formatArtistString(trackInfo.artists)}
          </p>
        </div>
        <div className="songOptionsDiv">
          <button className="songOptions"></button>
        </div>
      </div>
    );
  }
}

export default QueueItem;
